/* globals XMLHttpRequest, FormData, document */

import { loop } from './../utils/loop.js'

/**
 * This class is responsible for server communication using http requests
 */
export class Communication {
  /**
   * Creates an instance of Communication
   * @param {FormData} [form] An existing form that would be sent
   */
  constructor ( form ) {
    /**
     * The FormData to use when sending the request
     * @type {FormData}
     * @private
     */
    this.__form = new FormData( form )
    /**
     * The XMLHttpRequest to use in the request
     * @type {XMLHttpRequest}
     * @private
     */
    this.__request = new XMLHttpRequest()
    /**
     * Contains all the headers for the request
     * @type {Object}
     * @private
     */
    this.__headers = {}
  }

  /**
   * Adds a field to the request
   * @param {String} key Field name
   * @param {String} value Field value
   */
  addField ( key, value ) {
    this.__form.append( key, value )
  }

  /**
   * Adds a list of fields to the request
   * @param {Object} fields The object properties become the parameters and the values are the parameter values
   */
  addFields ( fields ) {
    loop( fields, ( value, key ) => {
      this.__form.append( key, value )
    } )
  }

  /**
   * Sets a header to the request
   * @param {String} name Header name
   * @param {String} value Header value
   */
  setHeader ( name, value ) {
    this.__headers[ name ] = value
  }

  /**
   * Sets a listener to track the progress of the request
   * @param {Function} [progress=null] This function has one parameter representing the progress value
   * @param {Function} end This function has one parameter which contains the responseText
   */
  listener ( progress, end ) {
    const self = this
    this.__request.onloadend = function ( ev ) {
      if ( self.__request.status === 200 ) {
        end( null, ev.target.responseText )
      } else {
        end( self.__request.status )
      }
    }
    if ( progress ) {
      this.__request.onprogress = function ( ev ) {
        if ( ev.lengthComputable ) {
          progress( ev.loaded / ev.total )
        } else {
          progress()
        }
      }
    }
  }

  /**
   * Parse a url to the correct format
   * @param {String} url The url to request to
   */
  parseUrl ( url ) {
    let div = document.createElement( 'div' )
    div.innerHTML = '<a></a>'
    div.firstChild.href = url // Ensures that the href is properly escaped
    return div.firstChild.href
  }

  /**
   * Abort a request
   */
  cancel () {
    this.__request.abort()
  }

  /**
   * Initiate the request
   * @param {String} url The url to request to
   * @param {String} method Either POST or GET
   */
  send ( url, method ) {
    url = this.parseUrl( url )
    this.__request.open( method.toUpperCase(), url )
    for ( let i = 0, k = Object.keys( this.__headers ), l = k.length; i < l; i++ ) {
      this.__request.setRequestHeader( k[ i ], this.__headers[ k[ i ] ] )
    }
    this.__request.send( this.__form )
  }

  /**
   * Initiate a POST request
   * @param {String} url The url to request to
   */
  post ( url ) {
    this.send( url, 'POST' )
  }

  /**
   * Initiate a GET request
   * @param {String} url The url to request to
   */
  get ( url ) {
    this.send( url, 'GET' )
  }
}

'use strict'

import { html } from './../utils/html.js'
import { uid } from './../utils/uid.js'
import { Router } from './router.js'

/**
 * This class is used to create web components
 */
export class Component {
  /**
   * The html string that becomes the view for this component
   */
  get template () {
    return ''
  }

  /**
   * The method gets called whenever the state changes
   * @param {String} url The current url
   */
  onStateChange ( url ) {}

  /**
   * The method gets called whenever the container is resized
   * @param {Number} width The width of the container
   * @param {Number} height The height of the container
   */
  onResize ( width, height ) {}

  /**
   * The method gets called when the component gets created in the page. It is the main method of the class
   */
  execute () {}

  /**
   * The method gets called when the component calls destroy(). It can be used to remove handlers or clear timeouts
   */
  terminate () {}

  /**
   * Creates an instance of Component
   * @param {HTMLElement} parent The parent wrapper
   * @param {Config} config A Config class instance
   * @param {Store} store A Store class instance
   */
  constructor ( parent, config, store ) {
    /**
     * A unique identifier
     * @type {String}
     * @public
     */
    this.uid = uid()
    /**
     * The parent wrapper
     * @type {HTMLElement}
     * @public
     */
    this.parent = parent
    /**
     * The component view
     * @type {HTMLElement}
     * @public
     */
    this.element = html( this.template )

    /**
     * The config class of the module parent
     * @type {Config}
     * @public
     */
    this.config = config
    /**
     * The store class of the module parent
     * @type {Store}
     * @public
     */
    this.store = store

    // Append element to the dom
    this.parent.appendChild( this.element )

    /**
     * The __stateWatcher is a handler for the router, it gets destroyed when the component gets destroyed
     * @type {String}
     * @private
     */
    this.__stateWatcher = Router.onStateChange( ( url ) => {
      this.__onStateChange( url )
    } )

    /**
     * Holds the previous width and height of the parent container
     * @type {Object}
     * @private
     */
    let previousParentSize = {}
    /**
     * Determines if the component watches for resize changes in parent dimensions
     * @type {Boolean}
     * @private
     */
    this.__resizeWatcher = true
    /**
     * This function checks for parent size changes every 100ms as long as the component exists
     * @type {Function}
     * @private
     */
    const parentResizeCheck = () => {
      setTimeout( () => {
        if ( this.__resizeWatcher ) {
          if ( previousParentSize.width !== this.parent.clientWidth || previousParentSize.height !== this.parent.clientHeight ) {
            previousParentSize.width = this.parent.clientWidth
            previousParentSize.height = this.parent.clientHeight
            this.__onResize( previousParentSize.width, previousParentSize.height )
          }
          parentResizeCheck()
        }
      }, 100 )
    }
    parentResizeCheck()
  }

  /**
   * The method gets called whenever the state changes
   * @param {String} url The current url
   * @private
   */
  __onStateChange ( url ) {
    this.onStateChange( url )
  }

  /**
   * The method gets called whenever the container is resized
   * @param {Number} width The width of the container
   * @param {Number} height The height of the container
   * @private
   */
  __onResize ( width, height ) {
    this.onResize( width, height )
  }

  /**
   * The method gets called when the component is destroyed
   */
  __destroy () {
    this.__stateWatcher()
    this.__resizeWatcher = false
    this.terminate()
    try {
      this.element.remove()
    } catch ( e ) {}
    delete this.parent.moduxComponent
  }
}

'use strict'

import { getter } from './../utils/getter.js'
import { setter } from './../utils/setter.js'

/**
 * Class holding static configuration
 */
class Config {
  /**
   * Creates an instance of Config
   */
  constructor () {
    /**
     * Contains all the configuration data
     * @type {Object}
     * @private
     */
    this.__configuration = {}
  }

  /**
   * Sets data in configuration
   * @param {String} key You can use sub data by concatenating strings with '.', eg: employee.name.firstname
   * @param {*} value The value of the property
   * @return { this }
   */
  set ( key, value ) {
    setter( key, value, this.__configuration )
    return this
  }

  /**
   * Gets data from configuration
   * @param {*} key You can use sub data by concatenating strings with '.', eg: employee.name.firstname
   */
  get ( key ) {
    return getter( key || '', this.__configuration )
  }

  /**
   * Returns an instance of Config
   * @return { Config }
   */
  create () {
    return new Config()
  }
}

/**
 * Instance of Config to be used as singleton
 */
export let config = new Config()

/* globals navigator, window */
'use strict'

import { loop } from './../utils/loop.js'
import { uid } from './../utils/uid.js'

/**
 * Class used to get device information
 */
export class Device {
  /**
   * Creates an instance of Device
   */
  constructor () {
    const ua = navigator.userAgent || navigator.vendor || window.opera

    let device = 'other'

    // get device
    if ( ua.match( /(iPhone)/ ) ) {
      device = 'iPhone'
    } else if ( ua.match( /(iPad)/ ) ) {
      device = 'iPad'
    } else if ( ua.match( /(iPod)/ ) ) {
      device = 'iPod'
    } else if ( ua.match( /(BlackBerry|BB10)/ ) ) {
      device = 'BlackBerry'
    } else if ( ua.match( /(IEMobile|windows phone)/ ) ) {
      device = 'WindowsMobile'
    } else if ( ua.match( /(Android)/ ) ) {
      device = 'Android'
    } else if ( ua.match( /(Macintosh)/ ) ) {
      device = 'Macintosh'
    } else if ( ua.match( /(Windows)/ ) ) {
      device = 'Windows'
    } else if ( ua.match( /(Linux)/ ) ) {
      device = 'Linux'
    }

    /**
     * Calls all the handlers bound to the window resize
     * @type {Function}
     * @private
     */
    this.__handlerListener = ( handler ) => {
      let orientation = ( window.innerHeight > window.innerWidth ) ? 'portrait' : 'landscape'
      handler( orientation )
    }

    /**
     * The handler for resize
     * @type {Function}
     * @private
     */
    this.__handlerResize = () => {
      loop( this.__listeners, ( handler ) => {
        this.__handlerListener( handler )
      } )
    }

    /**
     * Determines if any handlers are bound
     * @type {Boolean}
     * @private
     */
    this.__handlerBound = false

    /**
     * Holds the device type
     * @type {String}
     * @private
     */
    this.__device = device

    /**
     * Holds all the handlers bound
     * @type {Object}
     * @private
     */
    this.__listeners = {}
  }

  /**
   * Method used to attach a handler to the on resize event. Returns a function that can be called to remove the handler at a further date
   * @param {Function} listener A function containing one parameter which can be "landscape" or "portrait"
   * @return {Function}
   */
  onResize ( listener ) {
    let id = uid()
    this.__listeners[ id ] = listener
    if ( !this.__handlerBound ) {
      window.addEventListener( 'resize', this.__handlerResize )
      this.__handlerBound = true
    }

    this.__handlerListener( listener )

    return () => {
      delete this.__listeners[ id ]
      if ( Object.keys( this.__listeners ).length === 0 && this.__handlerBound ) {
        window.removeEventListener( 'resize', this.__handlerResize )
      }
    }
  }

  /**
   * Returns the device name
   * @return {String}
   */
  type () {
    return this.__device
  }

  /**
   * Returns true if the device is a desktop
   * @return {Boolean}
   */
  isDesktop () {
    return [ 'Windows', 'Linux', 'Macintosh', 'other' ].indexOf( this.__device ) !== -1
  }

  /**
   * Returns true if the device is a mobile device
   * @return {Boolean}
   */
  isMobile () {
    [ 'Windows', 'Linux', 'Macintosh', 'other' ].indexOf( this.__device ) === -1
  }
}

/* globals MutationObserver, HTMLElement */

'use strict'

import { loop } from './../utils/loop.js'
import { Router } from './router.js'

import { config } from './config.js'
import { store } from './store.js'

/**
 * The attribute name used to determine if an HTMLElement is a component
 */
const _attrComponent = 'data-modux-component'
/**
 * The attribute name used to determine if an HTMLElement ( usually an anchor tag ) is a state change component. This will prevent server reload on anchor tags.
 */
const _attrLink = 'data-modux-link'

/**
 * A shorthand for the link element handler
 * @param {Event} e
 */
const linkHandler = function ( e ) {
  let url = this.getAttribute( 'href' )
  if ( url ) {
    Router.redirect( url )
    e.preventDefault()
  }
}

/**
 * The main class, used to create a Modux module. You can think of this as the main application
 */
export class Module {
  /**
   * A method used to add components to the Module
   * @param {String} name The name under which the Component will be known as
   * @param {Component} dependency The Component to be added
   */
  addDependency ( name, dependency ) {
    this.__dependencies[ name ] = dependency
    return this
  }
  /**
   * A method used to remove components from the Module
   * @param {String} name The name of the Component to be removed
   */
  removeDependency ( name ) {
    if ( this.__dependencies[ name ] ) {
      delete this.__dependencies[ name ]
    }
    return this
  }

  /**
   * Creates an instance of Module
   * @param {String} [name] A unique name for the application, for easier management
   */
  constructor ( name ) {
    /**
     * A unique name for the application, for easier management
     * @type {String}
     */
    this.__name = name
    /**
     * Contains all the components added to the module
     * @type {Object}
     */
    this.__dependencies = {}
    /**
     * Contains a new instance of Config which is passed on to all components
     * @type {Config}
     */
    this.__config = config.create()
    /**
     * Contains a new instance of Store which is passed on to all components
     * @type {Store}
     */
    this.__store = store.create()
  }

  /**
   * Creates a component on an HTMLElement if it doesn't have one already
   * @param {HTMLElement} element The HTMLElement to bind the Component to
   * @param {Component} Component The Component to be bound
   */
  __createComponent ( element, Component ) {
    if ( !element.moduxComponent ) {
      element.moduxComponent = new Component( element, this.__config, this.__store )
      element.moduxComponent.execute()
    }
  }

  /**
   * Removes a component from an HTMLElement if it has one
   * @param {HTMLElement} element The HTMLElement for which we want to remove the Component
   */
  __removeComponent ( element ) {
    if ( element.moduxComponent ) {
      element.moduxComponent.__destroy()
    }
  }

  /**
   * Creates a link component on an HTMLElement if it doesn't have one already
   * @param {HTMLElement} element The HTMLElement to bind the link to
   */
  __createComponentLink ( element ) {
    if ( !element.moduxLink ) {
      element.addEventListener( 'click', linkHandler )
      element.moduxLink = true
    }
  }
  /**
   * Removes a link component from an HTMLElement if it has one
   * @param {HTMLElement} element The HTMLElement for which we want to remove the link
   */
  __removeComponentLink ( element ) {
    if ( element.moduxLink ) {
      element.removeEventListener( 'click', linkHandler )
      delete element.moduxLink
    }
  }

  /**
   * Checks if the current element needs a Component instance
   * @param {HTMLElement} node HTMLElement what we are checking
   * @param {String} attr The attribute to watch out for
   * @param {Function} handler The callback function which will be called after the checks are made
   */
  __loopOnElements ( node, attr, handler ) {
    if ( !( node instanceof HTMLElement ) ) {
      return
    }
    let name = node.getAttribute( attr )
    if ( name ) {
      handler( node, name )
    }
    loop( node.querySelectorAll( '*[' + attr + ']' ), ( element ) => {
      handler( element, element.getAttribute( attr ) )
    } )
  }

  /**
   * Initializes the Module on a specific HTMLElement and loads the specified Component for it
   * @param {HTMLElement} element The HTMLElement used as the wrapper for the Module
   * @param {Component} component The Component to be used as the main Component
   */
  bootstrap ( element, component ) {
    /**
     * Holds the MutationObserver which is used to check for changes in the DOM
     */
    this.__htmlWatcher = new MutationObserver( ( mutations ) => {
      mutations.forEach( ( mutation ) => {
        if ( mutation.type === 'attributes' ) {
          if ( mutation.attributeName === _attrComponent ) {
            this.__removeComponent( mutation.target )
            this.__createComponent( mutation.target, this.__dependencies[ mutation.target.getAttribute( _attrComponent ) ] )
          }
          if ( mutation.attributeName === _attrLink ) {
            this.__removeComponentLink( mutation.target )
            this.__createComponentLink( mutation.target )
          }
        }
        if ( mutation.type === 'childList' ) {
          if ( mutation.addedNodes.length > 0 ) {
            // Nodes that were added
            loop( mutation.addedNodes, ( node ) => {
              this.__loopOnElements( node, _attrComponent, ( e, attr ) => {
                if ( this.__dependencies[ attr ] ) {
                  this.__createComponent( e, this.__dependencies[ attr ] )
                }
              } )
              this.__loopOnElements( node, _attrLink, ( e ) => {
                this.__createComponentLink( e )
              } )
            } )
          }
          if ( mutation.removedNodes.length > 0 ) {
            // Nodes that were removed
            loop( mutation.removedNodes, ( node ) => {
              this.__loopOnElements( node, _attrComponent, ( e ) => {
                this.__removeComponent( e )
              } )
              this.__loopOnElements( node, _attrLink, ( e ) => {
                this.__removeComponentLink( e )
              } )
            } )
          }
        }
      } )
    } )
    this.__htmlWatcher.observe( element, { attributes: true, childList: true, characterData: false, subtree: true } )

    if ( !this.__dependencies[ component ] ) {
      throw new Error( 'Initial component cannot be found in dependency list' )
    }
    /**
     * Holds the main Component which is used for the Module.
     */
    this.__component = new this.__dependencies[ component ]( element, this.__config, this.__store )
    element.moduxComponent = this.__component
    element.moduxComponent.execute()
  }

  /**
   * Destroy the current module. This will also destroy all the components created and disconnect the MutationObserver
   */
  destroy () {
    this.__component.__destroy()
    this.__htmlWatcher.disconnect()
  }
}

/* globals window, document, history, location */

'use strict'

import { logger } from './../utils/logger.js'
import { loop } from './../utils/loop.js'
import { uid } from './../utils/uid.js'

/**
 * If set to true it will change the base tag every time a redirect occurs
 * @type {Boolean}
 */
let baseEnabled = false

/**
 * Holds all the handlers bound to the router class. Since the class is static, all imports will point to the same class
 * @type {Object}
 */
let listeners = {}

/**
 * This function is used to call all handlers on url or state change
 * @type {Function}
 */
const handler = function () {
  const url = location.pathname + location.search + location.hash
  logger.info( 'URL: ', url )

  if ( baseEnabled ) {
    let baseTag = document.head.querySelector( 'base' )
    if ( !baseTag ) {
      baseTag = document.createElement( 'base' )
      document.head.appendChild( baseTag )
    }
    baseTag.href = location.pathname
  }

  loop( listeners, ( listener ) => {
    listener( url )
  } )
}

window.addEventListener( 'pageshow', () => { handler() } )
window.addEventListener( 'hashchage', () => { handler() } )
window.addEventListener( 'popstate', () => { handler() } )
history.onpushstate = () => { handler() }

// Create the `pushstate` event
/**
 * This variable is a shorthand for history.pushState
 * @type {String}
 */
let pushState = history.pushState
/**
 * Add a new method to history, to call use when the state or url changes
 */
history.pushState = function () {
  let next = pushState.apply( history, arguments )
  if ( typeof history.onpushstate === 'function' ) {
    history.onpushstate()
  }
  return next
}

/**
 * A static class which is responsible for handling state and url change
 */
export class Router {
  /**
   * Determines if the base tag is modified each time a redirect occurs
   * @param {Boolean} enabled If true the base tag will be updated each time the pathname changes in the application
   */
  static setDynamicBase ( enabled ) {
    baseEnabled = !!enabled
  }

  /**
   * A static method used to push a new state
   * @param {String} url The new url
   */
  static redirect ( url ) {
    history.pushState( { url: url }, '', url )
  }

  /**
   * A static method used to attach a handler to the state change. It returns a function that can be called to remove the handler when it is no longer used
   * @param {Function} listener The state change handler
   * @return {Function}
   */
  static onStateChange ( listener ) {
    let id = uid()
    listeners[ id ] = listener
    return () => {
      delete listeners[ id ]
    }
  }
}

'use strict'

import { loop } from './../utils/loop.js'
import { uid } from './../utils/uid.js'

/**
 * A store class used to communicate between components
 */
class Store {
  /**
   * Creates an instance of Store
   */
  constructor () {
    /**
     * Contains all the listeners subscribed to the store
     * @type {Object}
     * @private
     */
    this.__listeners = {}
    /**
     * Contains all the data sent to the store. Is used when a handler is attached after the data has already been pushed.
     * @type {Object}
     * @private
     */
    this.__data = {}
  }

  /**
   * Method used to bind handlers to the store
   * @param {String} eventname The event name
   * @param {Function} listener The handler for the event
   * @param {Boolen} [showPreviousData=false] Set to true if data is to be sent to the handler upon attachment, if set to false it will work as a standard event handler
   * @return {Function} Call this function to unsubscribe from the event
   */
  on ( eventname, listener, showPreviousData ) {
    let id = uid()
    this.__listeners[ id ] = {
      eventname: eventname,
      handler: listener
    }
    if ( showPreviousData && this.__data && this.__data[ eventname ] !== undefined ) {
      listener.apply( listener, this.__data[ eventname ] )
    }
    return () => {
      delete this.__listeners[ id ]
    }
  }

  /**
   * Emits data on a specific event name. Accepts multiple parameters. The first parameter will be the event name and all the following params will be pushed on the event name
   */
  emit () {
    let values = Array.prototype.slice.call( arguments )
    let eventname = values.shift()

    this.__data[ eventname ] = values
    loop( this.__listeners, ( data ) => {
      if ( data.eventname === eventname ) {
        data.handler.apply( data.handler, values )
      }
    } )
  }

  /**
   * Returns an instance of Store
   * @return { Store }
   */
  create () {
    return new Store()
  }
}

/**
 * Instance of Store to be used as singleton
 */
export let store = new Store()

'use strict'

/**
 * Used to approximate a number to a certain number of decimals
 * @param {Numeric} num The number to be approximated
 * @param {Numeric} decimals The number of decimals to approximate to
 * @return {Numeric} A new number
 */
export let approx = ( num, decimals ) => {
  return parseFloat( parseFloat( num ).toFixed( decimals ) )
}

/* globals document */

'use strict'

/**
 * Set a browser cookie
 * @param {String} name Name of the cookie
 * @param {String} value Value of the cookie
 * @param {String} path The cookie path
 * @param {Numeric} seconds The cookie lifetime
 */
let setCookie = ( name, value, path, seconds ) => {
  let expires
  if ( seconds ) {
    let date = new Date()
    date.setTime( date.getTime() + ( seconds * 1000 ) )
    expires = '; expires=' + date.toGMTString()
  } else {
    expires = ''
  }
  document.cookie = name + '=' + value + expires + '; path=' + ( path || '/' )
}

/**
 * Returns the value of a browser cookie
 * @param {String} name Name of the cookie to be retrieved
 * @return {String} Returns the value of the cookie
 */
let getCookie = ( name ) => {
  name = name + '='
  var decodedCookie = decodeURIComponent( document.cookie )
  var cookies = decodedCookie.split( ';' )
  for ( var i = 0; i < cookies.length; i++ ) {
    var cookie = cookies[ i ]
    while ( cookie.charAt( 0 ) === ' ' ) {
      cookie = cookie.substring( 1 )
    }
    if ( cookie.indexOf( name ) === 0 ) {
      return cookie.substring( name.length, cookie.length )
    }
  }
  return ''
}

/**
 * Returns cookie manipulation functions
 */
export let cookie = {
  set: setCookie,
  get: getCookie
}

'use strict'

/**
 * An extenion of Date to DateTime
 */
export class DateTime extends Date {
  /**
   * Return the date and time in the YYYY-mm-DD HH:MM:ss format
   * @return {String}
   */
  toString () {
    return '' +
      this.getFullYear() +
      '-' +
      ( this.getMonth() + 1 ).toString().padStart( 2, '0' ) +
      '-' +
      this.getDate().toString().padStart( 2, '0' ) +
      ' ' +
      this.getHours().toString().padStart( 2, '0' ) +
      ':' +
      this.getMinutes().toString().padStart( 2, '0' ) +
      ':' +
      this.getSeconds().toString().padStart( 2, '0' )
  }

  /**
   * Returns the date and time in the HH:MM:ss format
