  play () {
    if ( !this.__enabled ) {
      return Promise.resolve()
    }

    let result = this.__audio.play()

    // Old browsers don't return a promise for play(), so we create one
    if ( !( result instanceof Promise ) ) {
      return Promise.resolve()
    }

    return result
  }

